arguments.assignment_info:
{%- set idx = dict() -%}
{%- for item in vars.input.params['device_data'] %}
  {%- set device = item.name -%}
  {%- set vname = item.mgt_vdom -%}
  {%- set vdom = 
         item.vdom
         |json_query('[?name == `{}`]'.format(vname))
  -%}
  {%- set ppkg = 
         vdom[0]['assignment info']
         |json_query('[?type == `policy`]')
  -%}
  {%- set prov = 
         vdom[0]['assignment info']
         |json_query('[?type != `policy` && type != `fext` && type != `wtp`]')
  -%}
  {%- for a in item['assignment info'] -%}
    {%- do prov.append(a) -%}
  {%- endfor -%}
  {%- do idx.update( 
    { 
      device: { 
        "ppkg":ppkg,
        "prov":prov
      } 
    } 
  ) -%}
{%- endfor -%}
{{idx}}

arguments.assignment_info_idx:
{%- set idx = dict() -%}
{%- for a in vars.assignment_info -%}

  {%- if vars.assignment_info[a].ppkg != None -%}

    {%- if vars.assignment_info[a].ppkg|length > 0 -%}

      {%- if "out-of-sync" in vars.assignment_info[a].ppkg|json_query('[].status') -%}
        {%- set pkg_state = "problem" -%}
      {%- elif "modified" in vars.assignment_info[a].ppkg|json_query('[].status') -%}
        {%- set pkg_state = "update needed" -%}
      {%- elif "unknown" in vars.assignment_info[a].ppkg|json_query('[].status') -%}
        {%- set pkg_state = "install needed" -%}
      {%- else -%}
        {%- set pkg_state = "ok" -%}
      {%- endif -%}

    {%- else -%}
      {%- set pkg_state = "no pkg" -%}
    {%- endif -%}
  {%- else -%}
    {%- set pkg_state = "no pkg" -%}
  {%- endif -%}

  {%- if vars.assignment_info[a].prov != None -%}

    {%- if vars.assignment_info[a].prov|length > 0 -%}

      {%- if "modified" in vars.assignment_info[a].prov|json_query('[].status') -%}
        {%- set prov_state = "update needed" -%}
      {%- else -%}
        {%- set prov_state = "ok" -%}
      {%- endif -%}

    {%- else -%}
      {%- set prov_state = "no templates" -%}
    {%- endif -%}
  {%- else -%}
    {%- set prov_state = "no templates" -%}
  {%- endif -%}
  {%- set _do = idx.update(
  {
    a : {
      "ppkg_state": pkg_state,
      "prov_state": prov_state,
      "ppkg": vars.assignment_info[a].ppkg,
      "prov": vars.assignment_info[a].prov
    }
  }) -%}
{%- endfor -%}
{{idx}}

arguments.db_status_msg:
