arguments.polpkg_status_msg:
{% set idx = dict() %}
{% for item in vars.input.params['device_data'] %}
    {%- set dev = item.name -%} 
    {%- set state = vars.assignment_info_idx[dev].ppkg_state -%} 
    {%- set clr = {
      "ok"             : "GREEN",
      "problem"        : "RED",
      "update needed"  : "ORANGE",
      "no pkg"         : "Grey"
      } 
    -%}
    {%- set lbl = {
      "ok"             : "OK",
      "problem"        : "Problem",
      "update needed"  : "Install Needed",
      "no pkg"         : "No Package"
      }
    -%}
    {%- set lblh = {
      "ok"             : vars.assignment_info_idx[dev].ppkg[0].name|default("missing") + " (" + vars.assignment_info_idx[dev].ppkg[0].status|default("missing") + ")",
      "problem"        : vars.assignment_info_idx[dev].ppkg[0].name|default("missing") + " (" + vars.assignment_info_idx[dev].ppkg[0].status|default("missing") + ")",
      "update needed"  : vars.assignment_info_idx[dev].ppkg[0].name|default("missing") + " (" + vars.assignment_info_idx[dev].ppkg[0].status|default("missing") + ")",
      "no pkg"         : "No Package"
      }
    -%}
    {% do idx.update( { dev : 
       { 
         "html" : '<span style="border:2px solid ' + clr[state]|default("GREY") + ';color:' + clr[state]|default("GREY") + ';border-radius:5px;background-color:black;font-family:verdana;font-weight:bold;padding-top:2px;padding-right:2px;padding-bottom:2px;padding-left:2px;font-size:10px;">' + lblh[state]|default("Unknown") + '</span>',
         "text" : lbl[state]|default("Unknown"),
         "assignment" : vars.assignment_info_idx[dev].ppkg[0].name | default("", True)
       }
    }) %}
{% endfor %}
{{idx}}

